import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import ReactDOM from "react-dom";
import Card from "react-bootstrap/Card";
import GetAllProducts from "../components/GetAllProducts";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";
import AdminAddProductAxios from "./AdminAddProductAxios";

function AdminEdit() {
  const [data, setdata] = useState([]);
  const [show, setShow] = useState(false);
  const [type, setType] = useState("frame");
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  let d;
  useEffect(() => {
    GetAllProducts.getAllProducts().then((value) => {
      setdata(value);
      d = value;
      console.log("d ", d);
      console.log("token :", sessionStorage.getItem("token"));
    });
  }, []);

  const cardItem = (item) => {
    return (
      <Card className="my-5 py-4" style={{ width: "32rem" }}>
        <div>
          <Card.Img
            src={item.productImg}
            variant="top"
            style={{ width: "30rem", height: "32rem" }}
            className="justify-content-center"
          />
        </div>
        <Card.Body className="text-center my-5">
          <Card.Title>{item.productName}</Card.Title>
          <Card.Text>{item.category}</Card.Text>
          <Card.Text>₹{item.price}</Card.Text>
          {/* <Card.Text>{item.description}</Card.Text> */}

          <NavLink
            to={"/AdminEdit/ProductDetails/" + item.productId}
            className="btn btn-outline-primary ms-2 fa fa-pencil"
          >
            {"    "}
            Edit Product
          </NavLink>
        </Card.Body>
      </Card>
    );
  };

  

  const addProducts = (event) => {
    console.log("Add Products");
    return (
      <div>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>ADD PRODUCT</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId="formBasicSelectCategory">
                <Form.Label>Select Category</Form.Label>
                <Form.Control
                  as="select"
                  value={type}
                  onChange={(e) => {
                    console.log("e.target.value", e.target.value);
                    setType(e.target.value);
                  }}
                >
                  <option value="frame">Frame</option>
                  <option value="glass">Glasses</option>
                  <option value="frameandglass">Frame + Glasses</option>
                  <option value="lenses">Lenses</option>
                  <option value="sunglasses">Sun Glasses</option>
                </Form.Control>
              </Form.Group>

              {(type == "frame" || type=="frameandglass") && (
                <div
                  className="my-3 "
                  style={{ backgroundColor: "rgba(235,228,227,1)" }}
                >
                  <h3>Frame</h3>
                  <Form.Group
                    className="mb-3 "
                    style={{ width: "400px" }}
                    controlId="formBasicFrameBrandName"
                  >
                    <Form.Label>Brand Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter Brand Name"
                      required
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    style={{ width: "400px" }}
                    controlId="formBasicFrameColor"
                  >
                    <Form.Label>Color</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter Color"
                      required
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="formBasicFrameDescription"
                    style={{ width: "400px" }}
                  >
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter Description"
                      required
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    style={{ width: "400px" }}
                    controlId="formBasicFramePrice"
                  >
                    <Form.Label>Price</Form.Label>
                    <Form.Control
                      type="number"
                      step="any"
                      placeholder="Enter Price"
                      required
                    />
                  </Form.Group>
                  <Form.Label>Select Shapes</Form.Label>
                  <div className="my-1">
                    <Form.Check
                      inline
                      label="rectangle"
                      name="group1"
                      id="formBasicFrameShapeRectangle"
                    />
                    <Form.Check
                      inline
                      label="oval"
                      name="group1"
                      id="formBasicFrameShapeOval"
                    />
                    <Form.Check
                      inline
                      label="round"
                      name="group1"
                      id="formBasicFrameShapeRound"
                    />
                    <Form.Check
                      inline
                      label="cateye"
                      name="group1"
                      id="formBasicFrameShapeCateye"
                    />
                  </div>
                  <Form.Label>Select Sizes</Form.Label>
                  <div className="my-1">
                    <Form.Check
                      inline
                      label="large"
                      name="group1"
                      id="formBasicFrameSizeLarge"
                    />
                    <Form.Check
                      inline
                      label="medium"
                      name="group1"
                      id="formBasicFrameSizeMedium"
                    />
                    <Form.Check
                      inline
                      label="small"
                      name="group1"
                      id="formBasicFrameSizeSmall"
                    />
                  </div>
                </div>
              )}
              {(type == "glass" || type=="frameandglass") && (
                <div
                  className="my-3 "
                  style={{ backgroundColor: "rgba(235,228,227,1)" }}
                >
                  <h3>Glass</h3>
                  <Form.Group
                    className="mb-3 "
                    style={{ width: "400px" }}
                    controlId="formBasicGlassBrandName"
                  >
                    <Form.Label>Brand Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter Brand Name"
                      required
                    />
                  </Form.Group>

                  <Form.Group
                    className="mb-3"
                    style={{ width: "400px" }}
                    controlId="formBasicGlassPrice"
                  >
                    <Form.Label>Price</Form.Label>
                    <Form.Control
                      type="number"
                      step="any"
                      placeholder="Enter Price"
                      required
                    />
                  </Form.Group>
                  <Form.Label>Select Power Range</Form.Label>
                  <div className="my-1">
                    <Form.Check
                      inline
                      label="+/-10"
                      name="group1"
                      id="formBasicGlassPowerRange+/-10"
                    />
                    <Form.Check
                      inline
                      label="+/-8"
                      name="group1"
                      id="formBasicGlassPowerRange+/-8"
                    />
                    <Form.Check
                      inline
                      label="+/-4"
                      name="group1"
                      id="formBasicGlassPowerRange+/-4"
                    />
                    <Form.Check
                      inline
                      label="0"
                      name="group1"
                      id="formBasicGlassPowerRange0"
                    />
                  </div>
                  <Form.Label>Select Type</Form.Label>
                  <div className="my-1">
                    <Form.Check
                      inline
                      label="Zero Power"
                      name="group1"
                      id="formBasicGlassTypeZeroPower"
                    />
                    <Form.Check
                      inline
                      label="Digital Screen Protection"
                      name="group1"
                      id="formBasicGlassTypeDigitalScreenProtection"
                    />
                    <Form.Check
                      inline
                      label="Single Vision"
                      name="group1"
                      id="formBasicGlassTypeSingleVision"
                    />
                    <Form.Check
                      inline
                      label="Bifocal"
                      name="group1"
                      id="formBasicGlassTypeBifocal"
                    />
                  </div>
                </div>
              )}
              {(type == "lenses" ) && (
                <div
                  className="my-3 "
                  style={{ backgroundColor: "rgba(235,228,227,1)" }}
                >
                  <h3>Lenses</h3>
                  <Form.Group
                    className="mb-3 "
                    style={{ width: "400px" }}
                    controlId="formBasicLensesBrandName"
                  >
                    <Form.Label>Brand Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter Brand Name"
                      required
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    style={{ width: "400px" }}
                    controlId="formBasicLensesColor"
                  >
                    <Form.Label>Color</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter Color"
                      required
                    />
                  </Form.Group>
                  
                  <Form.Group
                    className="mb-3"
                    style={{ width: "400px" }}
                    controlId="formBasicLensesPrice"
                  >
                    <Form.Label>Price</Form.Label>
                    <Form.Control
                      type="number"
                      step="any"
                      placeholder="Enter Price"
                      required
                    />
                  </Form.Group>
