import React, { useState, useEffect } from "react";
import GetAllProducts from "../components/GetAllProducts";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useParams } from "react-router";
import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";
import { addItem, delItem, allItem } from "../redux/actions/index";
import Form from "react-bootstrap/Form";

import AddItemsAxios from "./AddItemsAxios";
import DeleteItemsAxios from "./DeleteItemsAxios";

function ProductDetail() {
  const [data, setdata] = useState([]);
  const [cartBtn, setCartBtn] = useState("Add to Cart");
  const [qty, setqty] = useState(0);
  let d;

  // We need to store useDispatch in a variable
  const dispatch = useDispatch();

  useEffect(() => {
    GetAllProducts.getAllProducts().then((value) => {
      setdata(value);
      d = value;
      console.log("dygwewfiuhhfoiqhofioehfoiehfiwe ", value);
    });
  }, []);
  const proid = useParams();
  console.log("proid: ", proid.id);
  console.log("data: ", data);
  const proDetail = data.filter((x) => x.productId == proid.id);
  console.log("productDetail: ", proDetail);
  const product = proDetail[0];
  console.log(product);

  const func = () => {
    const handleCart = (product) => {
      let result;
      if (sessionStorage.getItem("token") !== "") {
        if (cartBtn === "Add to Cart") {
          AddItemsAxios.addItemsAxios(
            "http://localhost:8001/allowed//items/" +
              product.productId +
              "/" +
              qty
          ).then((value) => {
            result = value[0];
            console.log("result ", result);
            console.log("product ", product);
            dispatch(addItem(result));
            setCartBtn("Remove from Cart");
          });
        } else {
          DeleteItemsAxios.deleteItemsAxios(
            "http://localhost:8084/cart-service/cart/updateCart/deleteItem/" +
              product.productName
          );

          dispatch(delItem(product));
          setCartBtn("Add to Cart");
        }
      } else {
        alert("PLEASE LOGIN/SIGNUP TO ADD ITEMS TO CART");
      }
    };

    const funcSearch = (event) => {
      event.preventDefault();
      setqty(document.getElementById("Quantity").value);
      console.log(qty);
    };

    return (
      <div>
        <Container className="my-5 py-3">
          {
            <Row>
              <Col className="d-flex justify-content-center mx-auto product">
                <img
                  src={product.productImg}
                  alt={""}
                  height="600px"
                />
              </Col>
              <Col className="d-flex flex-column justify-content-center">
        {product.category=="frame" &&
          <>
           <h1>Brand Name: {product.frame.brandName}</h1>
           <h3>Price: ₹{product.frame.price}</h3>
           <h5>Category: {(product.category).toUpperCase()}</h5>
           <h5>Color: {product.frame.color}</h5>
           <h5>Description: {product.frame.description}</h5>
           
        <h5 className="my-3">Available Sapes :</h5>
        <div key={`inline-radio`} className="mb-3">
        {(product.frame.shape.filter((x) => x == "rectangle").length>0)&&
          <Form.Check
            inline
            label="Rectangle"
            name="group1"
            type="radio"
            id="formBasicRadioShapeRectangle"
          />}
        {(product.frame.shape.filter((x) => x == "oval").length>0)&&
          <Form.Check
            inline
            label="Oval"
            name="group1"
            type="radio"
            id="formBasicRadioShapeOval"
          />}
        {(product.frame.shape.filter((x) => x == "round").length>0)&&
          <Form.Check
            inline
            label="Round"
            name="group1"
            type="radio"
            id="formBasicRadioShapeRound"
          />}
        {(product.frame.shape.filter((x) => x == "cateye").length>0)&&
          <Form.Check
            inline
            label="Cat Eye"
            name="group1"
            type="radio"
            id="formBasicRadioShapeCateye"
          />}
        
        </div>
        <h5 className="my-3">Available Sizes :</h5>
        <div key={`inline-radio`} className="mb-3">
        {(product.frame.size.filter((x) => x == "large").length>0)&&
          <Form.Check
            inline
            label="Large"
            name="group1"
            type="radio"
            id="formBasicRadioSizeLarge"
          />}
        {(product.frame.size.filter((x) => x == "medium").length>0)&&
          <Form.Check
            inline
            label="Medium"
            name="group1"
            type="radio"
            id="formBasicRadioSizeMedium"
          />}
        {(product.frame.size.filter((x) => x == "small").length>0)&&
          <Form.Check
            inline
            label="Small"
            name="group1"
            type="radio"
            id="formBasicRadioSizeSmall"
          />}
        </div>
        </>
        }
           {/* {item.category=="glass" &&
          <>
           <Card.Text><h4>Brand Name: {item.glass.brandName}</h4></Card.Text>
           <Card.Text><h4>Price: ₹{item.glass.price}</h4></Card.Text>
           
           </>
           }
           {item.category=="frameandglass" &&
          <>
           <Card.Text><h3>FRAME</h3></Card.Text>
           <Card.Text><h4>Brand Name: {item.frame.brandName}</h4></Card.Text>
           <Card.Text>Color: {item.frame.color}</Card.Text>
           <Card.Text><h4>Price: ₹{item.frame.price}</h4></Card.Text>
           <Card.Text>Description: {item.frame.description}</Card.Text>
           

           <Card.Text><h3>GLASS</h3></Card.Text>
           <Card.Text><h4>Brand Name: {item.glass.brandName}</h4></Card.Text>
           <Card.Text><h4>Price: ₹{item.glass.price}</h4></Card.Text>
           </>
           
           }
           
           {item.category=="lenses" &&
          <>
           <Card.Text><h4>Brand Name: {item.lenses.brandName}</h4></Card.Text>
           <Card.Text>Color: {item.lenses.color}</Card.Text>
           <Card.Text><h4>Price: ₹{item.lenses.price}</h4></Card.Text>
           <Card.Text>Quantity in Box: {item.lenses.qtyInBox}</Card.Text>
           <Card.Text>Shape: {item.lenses.shape}</Card.Text>
           </>
           }
           {item.category=="sunglasses" &&
          <>
           <Card.Text><h4>Brand Name: {item.sunGlasses.brandName}</h4></Card.Text>
           <Card.Text>FrameColor: {item.sunGlasses.frameColor}</Card.Text>
           <Card.Text>FrameShape: {item.sunGlasses.frameShape}</Card.Text>
           <Card.Text>GlassColor: {item.sunGlasses.glassColor}</Card.Text>
           <Card.Text><h4>Price: ₹{item.sunGlasses.price}</h4></Card.Text>
           <Card.Text>Weight: ₹{item.sunGlasses.weight}</Card.Text>
           </>
           } */}
                {product.stockQuantity <= 0 && (
                  <div>
                    <h2 className="f-bolder" style={{ color: "red" }}>
                      SORRY OUT OF STOCK !
                    </h2>
                    <p className="f-bolder" style={{ color: "green" }}>
                      Pre book this item to get it as soon as it is back in
                      stock .
                    </p>
                  </div>
                )}
                <Container>
                  <Row className="d-flex">
                    <Col className="text-left d-flex">
                      {product.stockQuantity > 0 && (
                        <Form className="d-flex ms-5">
                          <Form.Group
                            className="d-flex ms-5"
                            controlId="Quantity"
                          >
                            <Form.Label
                              className="d-flex fw-bolder"
                              style={{ fontSize: "35px" }}
                            >
                              QUANTITY :
                            </Form.Label>
                            <Form.Control
                              className="ms-5"
                              type="number"
                              placeholder="Quantity"
                              style={{ width: "10rem" }}
                              onChange={funcSearch}
                            />
                          </Form.Group>
                        </Form>
                      )}
                    </Col>
                  </Row>
                </Container>

                <Button
                  onClick={() => handleCart(product)}
                  variant="outline-primary my-5"
                  className="fa fa-cart-plus"
                >
                  {"    "}
                  {cartBtn}
                </Button>
              </Col>
            </Row>
          }
        </Container>
      </div>
    );
  };

  return React.Children.toArray(data.map(func)[0]);
}
export default ProductDetail;
