import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import QuerySaveAxios from "./QuerySaveAxios";
import QueryGetAllAdminEmailAxios from "./QueryGetAllAdminEmailAxios";
import PasswordSendEmailAxios from "./PasswordSendEmailAxios";

const Contact = () => {
  const saveQuery = (event) => {
    event.preventDefault();
    alert("PROCESSING PLEASE WAIT")
    let body={
      fullName:document.getElementById("exampleFormName").value,
      username:document.getElementById("exampleFormEmail").value,
      query:document.getElementById("exampleForm.ControlTextareaQuery").value,
    }
    QuerySaveAxios.QuerySaveAxios(body).then(() => {
     
      QueryGetAllAdminEmailAxios.QueryGetAllAdminEmailAxios().then((value1) => {
        value1.map(sendEmail)
      })
      
    })
  }

  const sendEmail = (id) => {
    let sub="New Query Eshopping Cart"
    let body="Full name:  "+document.getElementById("exampleFormName").value+"          "+"Email ID :  "+document.getElementById("exampleFormEmail").value+"        "+"Query :  "+document.getElementById("exampleForm.ControlTextareaQuery").value
    PasswordSendEmailAxios.PasswordSendEmailAxios(id,sub,body).then(() => {
      alert("Thanks for writing to us. We will get to you back soon")
    })
  }
  return (
    <>
    <Container className="mb-5">
      <Row>
        <Col className="text-center py-4 my-4">
          <h1>Have Some Questions?</h1>
          <hr />
        </Col>
      </Row>
      <Row>
        <Col className="d-flex justify-content-center">
          <img
            src="/assets/images/contact.jpg"
            alt="Contact Us"
            height="400px"
            width="550px"
          />
        </Col>
        <Col>
          <Form onSubmit={saveQuery}>
            <Form.Group className="mb-3" controlId="exampleFormName">
              <Form.Label>Full Name</Form.Label>
              <Form.Control type="text" placeholder="FirstName LastName" required/>
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleFormEmail">
              <Form.Label>Email Address</Form.Label>
              <Form.Control type="email" placeholder="name@example.com" required/>
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextareaQuery"
              
            >
              <Form.Label>Query</Form.Label>
              <Form.Control as="textarea" rows={3} required/>
            </Form.Group>
            <Button variant="outline-primary ms-2" type="submit">
              Submit
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
    <Container style={{ height: "215px" }}></Container>
    </>
  );
};

export default Contact;
