import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import axios from "axios";
import PasswordSendEmailAxios from "../PasswordSendEmailAxios";
import TestValidEmailAxios from "../TestValidEmailAxios";
import PasswordResetDataAxios from "../PasswordResetDataAxios";


let resetCode=0;
function Login() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  

  return (
    <>
      {/* Button trigger modal */}
      <Button id="testLogin" variant="outline-primary ms-2" onClick={handleShow}>
        <span className="fa fa-sign-in me-1"> Login</span>
      </Button>

      {/* Modal */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* <Button variant="primary w-100 mb-4">
            <span className="fa fa-google me-2"> Sign in with Google</span>
          </Button>
          <Button variant="primary w-100 mb-4">
            <span className="fa fa-facebook-official me-2">
              {" "}
              Sign in with Facebook
            </span>
          </Button> */}
          <Form onSubmit={authenticate}>
            <Form.Group className="mb-3" controlId="formBasicEmail" id="formBasicEmail1">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email" required />
              <Form.Text className="text-muted">
                We'll never share your email with anyone else.
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" id="formBasicPassword1" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" required />
            </Form.Group>
            
            <Button className="onlyTextButton" id="forgotPasword1"
              variant="light"
              onClick={resetPassword}
            >
              Forgot your Password ?
            </Button>
            <Form.Group className="mb-3" id="formBasicVerify1" controlId="formBasicVerify"style={{ display: "none" }}>
              <Form.Label>Verification</Form.Label>
              <Form.Control type="number" placeholder="Enter the 6 digit verification code sent to your email"  />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPasswordNew" id="formBasicPasswordNew1"style={{ display: "none" }}>
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Enter new Password"  />
            </Form.Group>

            <Button
              variant="outline-primary w-100 mt-5 fa fa-share"
              id="verifybutton1"
              style={{ display: "none" }}
              onClick={ResetDataPassword}
            >
              {' '}Verify
            </Button>

            <Button
              variant="outline-primary w-100 mt-5 fa fa-share"
              id="resetPasswordbutton1"
              style={{ display: "none" }}
              onClick={ResetPasswordDataComplete}
            >
              {' '}Reset Password
            </Button>


            <Button
              variant="outline-primary w-100 mt-5 fa fa-share"
              type="submit"
              id="submitbutton1"
            >
              {' '}Submit
            </Button>
            <div id="formError" style={{ display: "none" }}>
              <p style={{ color: "red", fontWeight: "bolder" }}>
                Wrong Credentials! Please try again... !
              </p>
            </div>
            <div id="formError1" style={{ display: "none" }}>
              <p style={{ color: "red", fontWeight: "bolder" }}>
                Invalid Verification Code Please try again... !
              </p>
            </div>
            <div id="formError2" style={{ display: "none" }}>
              <p style={{ color: "red", fontWeight: "bolder" }}>
                No such User Exists Please try again... !
              </p>
            </div>
            <div id="loading" style={{ display: "none" }}>
              <div className="d-flex">
              <p className="fa fa-spinner fa-spin fa-2x fa-fw" style={{ color: "blue", fontWeight: "bolder"  }}>
              </p>
              <p className="px-5" style={{ color: "blue", fontWeight: "bold" ,fontSize: "20px" }}>
                {' '}Sending Email Please wait !
              </p>
              </div>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
}

async function authenticate(event) {
  let token;
  let axiosResponse;
  
  event.preventDefault();
  if (
    sessionStorage.getItem("token") === "" ||
    sessionStorage.getItem("token") === null
  ) {
    let email = document.getElementById("formBasicEmail").value;
    let password = document.getElementById("formBasicPassword").value;
    console.log(email);
    console.log(password);

    let url = "http://localhost:8001/authenticate";
    let body = { username: email, password: password };
    console.log(body);

    let error = "";
    try {
      axiosResponse = await axios.post(url, body).catch((err) => {
        if (err.response.status !== 200) {
          console.log(err.response.status);
          error = err.response.status;
          throw new Error("WRONG CREDENTIALS PLEASE TRY AGAIN");
        }
        throw err;
      });
    } catch (err) {
      if (document.getElementById("formError").style.display == "none") {
        document.getElementById("formError").style.display = "block";
      }
    }
    if (error === "") {
      token = axiosResponse.data;
      console.log(axiosResponse);
      console.log(token.jwtToken);
      alert("SUCCESSFULLY LOGGED IN");
      sessionStorage.setItem("token", token.jwtToken);
      window.open("http://localhost:3000/", "_self");
    }
  } else {
    alert("YOU ARE ALREADY LOGGED IN");
  }
}

function resetPassword()
{

 TestValidEmailAxios.TestValidEmailAxios(document.getElementById("formBasicEmail").value).then((value100) => {

  console.log(value100)
 if(value100==="valid"){ 
 document.getElementById("formError2").style.display = "none"
 let vfcode= Math.floor(100000 + Math.random() * 900000);
 resetCode=vfcode
 document.getElementById("formBasicPassword1").style.display = "none"
 document.getElementById("forgotPasword1").style.display = "none"

 let id=document.getElementById("formBasicEmail").value;
 let sub="E shopping cart Password Reset"
 let body="Your One Time Verification code for Password Reset is "+vfcode+" Do not share this with anyone"

 alert("A One Time Verification Code Will be sent to your email account");
 document.getElementById("formBasicEmail1").style.display = "none"
 document.getElementById("submitbutton1").style.display = "none"
 document.getElementById("loading").style.display = "block"

 PasswordSendEmailAxios.PasswordSendEmailAxios(id,sub,body).then(() => {

  document.getElementById("loading").style.display = "none"
  document.getElementById("formBasicVerify1").style.display = "block"
  document.getElementById("formBasicEmail1").style.display = "none"
  document.getElementById("submitbutton1").style.display = "none"
  document.getElementById("verifybutton1").style.display = "block"
 })
 }
 else{
  document.getElementById("formError2").style.display = "block"
 }
})
}

function ResetDataPassword()
{
  let inputvfcode=document.getElementById("formBasicVerify").value;
  if(inputvfcode==resetCode)
  {
  document.getElementById("formBasicPasswordNew1").style.display = "block"
  document.getElementById("resetPasswordbutton1").style.display = "block"
  document.getElementById("verifybutton1").style.display = "none"
  document.getElementById("formError1").style.display = "none"

  }
  else
  {
    document.getElementById("formError1").style.display = "block"

  }
}

function ResetPasswordDataComplete()
{
  let id=document.getElementById("formBasicEmail").value;
  let password=document.getElementById("formBasicPasswordNew").value;
  PasswordResetDataAxios.PasswordResetDataAxios(id,password).then(() => {

    alert("ACCOUNT PASSWORD RESET SUCCESSFUL")
    window.open("http://localhost:3000","_self")
  })
}


export default Login;
